<?phpFunction Connexion($connexion,$log,$mdp) {	if (!isset($_SESSION["Loglogin"]))		{							if (isset($log) and $mdp) 		{		$ad = new admin($connexion);								$connexionadmin = $ad->connexionLog($log,$mdp);		$nbdecompte=count($connexionadmin);											if ($nbdecompte==1)				{				$_SESSION["Loglogin"]=$log ;				$_SESSION["Mauxdepasse"]=$mdp ;																						$_SESSION["Niveau"] = ($connexionadmin["0"]["niveau"]) ;				$_SESSION["photo"]=($connexionadmin["0"]["Avatar"]);															echo "<script langage=javascript>				window.location.replace('index.php');</script> ";														}				else				{					echo "<script langage=javascript>				alert('erreur de connexion')  				dow.location.replace('index.php?vue=vue_connexion.php');				</script> ";										}		}	}}						?>