<?phpFunction Connexion($connexion,$log,$mdp) {	if (!isset($_SESSION["Loglogin"]))		{							if (isset($log) and $mdp) 		{		$ad = new admin($connexion);								$connexionadmin = $ad->connexionLog($log,$mdp);		$nbdecompte=count($connexionadmin);											if ($nbdecompte==1)				{				$_SESSION["Loglogin"]=$log ;				$_SESSION["Mauxdepasse"]=$mdp ;																						$_SESSION["Niveau"] = ($connexionadmin["0"]["niveau"]) ;				$_SESSION["photo"]=($connexionadmin["0"]["avatar"]);															//echo "<script langage=javascript>				//window.location.replace('index.php');</script> ";														}				else				{					echo "<script langage=javascript>				alert('erreur de connexion')  				dow.location.replace('index.php?vue=vue_connexion.php');				</script> ";										}		}	}}function encrypt_decrypt($action, $string) {    $output = false;    $encrypt_method = "AES-256-CBC";    $secret_key = 'This is my secret key';    $secret_iv = 'This is my secret iv';    // hash    $key = hash('sha256', $secret_key);        // iv - encrypt method AES-256-CBC expects 16 bytes - else you will get a warning    $iv = substr(hash('sha256', $secret_iv), 0, 16);    if( $action == 'encrypt' ) {        $output = openssl_encrypt($string, $encrypt_method, $key, 0, $iv);        $output = base64_encode($output);    }    else if( $action == 'decrypt' ){        $output = openssl_decrypt(base64_decode($string), $encrypt_method, $key, 0, $iv);    }    return $output;}						?>